import React from 'react';
import Comment from './Comment';
import EditComponent from './EditComponent';
import { connect } from 'react-redux';

class ShowComment extends React.Component{
    render(){
        return(
            <div>
                <h1>Show All Comment</h1>
                <div className = 'row'>
                    <div className = 'col-1'></div>
                    <div className = 'col-3 center'>NAME</div>
                    <div className = 'col-2'>GENDER</div>
                    <div className = 'col-2'>MOBILE PHONE</div>
                    <div className = 'col-2'>NATIONALITY</div>
                    <div className = 'col-2'></div>
                </div>
                {console.log(this.props.commentcard)}
                {this.props.commentcard.map((comment) => (
                <div key = {comment.id}>
                    {comment.editing ? <EditComponent comment = {comment} key={comment.id} /> :
                    <Comment key={comment.id} comment = {comment} />}
                </div>
                ))}
            </div>
        );
    }
}

const mapStateToProps = state => {
    return{
        commentcard : state
    }
} 

export default connect(mapStateToProps)(ShowComment);
