import React from 'react';

class Comment extends React.Component{
    render(){
        return(
            <div>
                <h2>{this.props.comments.name}</h2>
                <div>
                    <p>
                        {this.props.comments.message}
                        <button>EDIT</button>
                        <button>DELETE</button>
                    </p>
                </div>
            </div>
        );
    };
}

export default Comment;
