import React from 'react';
import { connect } from 'react-redux';

class Information extends React.Component{
    render(){
        return(
        <div className = 'container' style={{backgroundColor:'white', borderRadius:'10px'}}>
            <div className = 'row' style = {{backgroudColor:'lightblue'}}>
                <div className = 'col-1' >
                    <label>
                        <input type="checkbox" checked="checked" />
                        <span>Yellow</span>
                    </label>
                </div>
                <div className = 'col-3 center'>{this.props.info.firstname}{' '}{this.props.info.lastname}</div>
                <div className = 'col-2 center'>{this.props.info.gender}</div>
                <div className = 'col-2 center'>{this.props.info.mobileno}</div>
                <div className = 'col-2 center setuppercase'>{this.props.info.nation}</div>
                <div className = 'col-2'>
                    <button onClick = {() => this.props.dispatch({type: 'EDI_INFO', id: this.props.info.id })}>
                    EDIT
                    </button>
                    <button onClick = {() => this.props.dispatch({type: 'DEL_INFO', id: this.props.info.id })}>
                    DELETE
                    </button>
                </div>
            </div>
        </div>    
        );
    };
}

export default connect()(Information);
