import React from 'react';
import { connect } from 'react-redux';

class Comment extends React.Component{
    render(){
        return(
            <div>
                <h2>{this.props.comment.name}</h2>
                <p>{this.props.comment.message}</p>
                <button onClick = {() => this.props.dispatch({type: 'EDI_COMMENT', id: this.props.comment.id })}>
                    EDIT
                </button>
                <button onClick = {() => this.props.dispatch({type: 'DEL_COMMENT', id: this.props.comment.id })}>
                    DELETE
                </button>
            </div>
        );
    };
}

export default connect()(Comment);
