import React from 'react';
import Comment from './Comment';
import EditComponent from './EditComponent';
import ObjectTable from 'react-object-table';
import { connect } from 'react-redux';

class ShowComment extends React.Component{

    handleUpdate(id, values) {
        this.setState(prevState => {
          const stateChanges = {
            objects: prevState.objects,
          }
          prevState.objects.map((object, index) => {
            if (object.id === id) {
              stateChanges.objects[index] = {
                ...object,
                ...values,
              };
            }
          });
          return stateChanges;
        });
      }

    render(){
        return(
            <div>
                <h1>Show All Comment</h1>
                <ObjectTable
                columns={this.props.columns}
                objects={this.state.commentcard}
                onUpdate={this.handleUpdate.bind(this)}
                />
                {console.log(this.props.commentcard)}
                {this.props.commentcard.map((comment) => (
                <div key = {comment.id}>
                    {comment.editing ? <EditComponent comment = {comment} key={comment.id} /> :
                    <Comment key={comment.id} comment = {comment} />}
                </div>
                ))}
            </div>
        );
    }
}

SimpleTable.defaultProps = {
    columns: [
      {
        name: 'First Name',
        key: 'firstName',
      },
      {
        name: 'Last Name',
        key: 'lastName',
      },
    ],
  };

const mapStateToProps = state => {
    return{
        commentcard : state
    }
} 

export default connect(mapStateToProps)(ShowComment);
