import React from 'react';
import { connect } from 'react-redux';
import { Button, DropdownButton, Dropdown } from 'react-bootstrap';

class CommentForm extends React.Component{
    handlesubmit = (e) => {
        e.preventDefault();
        const name = this.getName.value;
        const message = this.getMessage.value;
        const data = {
            id: new Date(),
            name,
            message,
            editing: false
        };
        this.props.dispatch({   
            type: 'ADD_COMMENT', 
            data
        });

        this.getName.value = '';
        this.getMessage.value = '';
    }

    render(){
        return(
            <div>
                <h1>Personal Information</h1>
                <div className = "Title">
                <p>Title</p>
                <DropdownButton id="dropdown-basic-button" title={Dropdown.Item.title}>
                    <Dropdown.Item title = "Mr." >Mr.</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Ms./Mrs.</Dropdown.Item>
                </DropdownButton>
                </div>
                <form onSubmit = {this.handlesubmit}>
                    <input required type="text" placeholder = "Enter Name" ref = {(input)=> this.getName = input} />
                    <br />
                    <br />
                    <textarea  rows="5" cols="28" placeholder="Messages" ref = {(input)=> this.getMessage = input} />
                    <br />
                    <br />
                    <Button>Comment</Button>
                </form>
            </div>
        );
    };
}

export default connect()(CommentForm);
