import React from 'react';
import { connect } from 'react-redux';

class Comment extends React.Component{
    render(){
        return(
            <div className = 'row' style = {{backgroudColor:'lightblue'}}>
                <div className = 'col-1' ></div>
                <div className = 'col-3 center'>{this.props.comment.name}</div>
                <div className = 'col-2 center'>{this.props.comment.message}</div>
                <div className = 'col-2 center'><h2>MOBILE PHONE</h2></div>
                <div className = 'col-2 center'><h2>NATIONALITY</h2></div>
                <div className = 'col-2'></div>
                <button onClick = {() => this.props.dispatch({type: 'EDI_COMMENT', id: this.props.comment.id })}>
                    EDIT
                </button>
                <button onClick = {() => this.props.dispatch({type: 'DEL_COMMENT', id: this.props.comment.id })}>
                    DELETE
                </button>
            </div>
        );
    };
}

export default connect()(Comment);
