import React from 'react';
import { connect } from 'react-redux';
import { Button, Form } from 'react-bootstrap';

class CommentForm extends React.Component{
    handlesubmit = (e) => {
        e.preventDefault();
        const name = this.getName.value;
        const message = this.getMessage.value;
        const data = {
            id: new Date(),
            name,
            message,
            editing: false
        };
        this.props.dispatch({   
            type: 'ADD_COMMENT', 
            data
        });

        this.getName.value = '';
        this.getMessage.value = '';
    }

    render(){
        return(
            <div className='container'>
                <h1>Personal Information</h1>
                <div className='row' id = '1strow'>
                    <div className = 'col-sm-2'>
                        <div class="form-group row">
                            <label class="col-sm-2 col-form-label">Title:</label>
                            <div class="col-10">
                                <select class="form-control">
                                <option>Mr.</option>
                                <option>Ms./Mrs.</option>
                                </select>
                            </div>
                        </div>
                    </div> 
                    <div className = 'col-sm-5'>
                        <div class="form-group row">
                            <label class="col-sm-2 col-form-label">Firstname:</label>
                            <div class="col-10">
                                <input type="text" class="form-control" id="firstname" />
                            </div>
                        </div>
                    </div>  
                    <div className = 'col-sm-5'>
                        <div class="form-group row">
                            <label class="col-sm-2 col-form-label">Lastname:</label>
                            <div class="col-10">
                                <input type="text" class="form-control" id="lastname" />
                            </div>
                        </div>
                    </div> 
                </div>
                <div className='row' id = '2ndrow'>
                    <div className = 'col-md-4'>
                        <div class="form-group row">
                            <label class="col-sm-6 col-form-label">Birthday:</label>
                            <div class="col-6">
                                <Form.Group controlId="dob">
                                <Form.Control type="date" name="dob" placeholder="Date of Birth" />
                                </Form.Group>
                            </div>
                        </div>
                    </div>  
                </div>
                <form onSubmit = {this.handlesubmit}>
                    <input required type="text" placeholder = "Enter Name" ref = {(input)=> this.getName = input} />
                    <br />
                    <br />
                    <textarea  rows="5" cols="28" placeholder="Messages" ref = {(input)=> this.getMessage = input} />
                    <br />
                    <br />
                    <Button>Comment</Button>
                </form>
            </div>
        );
    };
}

export default connect()(CommentForm);
