import React from 'react';
import { connect } from 'react-redux';

class EditComponent extends React.Component{
    handlesubmit = (event) => {
        event.preventDefault();
        const newname = this.getName.value;
        const newmessage = this.getMessage.value;
        const data = {
            newname,
            newmessage
        };

        this.props.dispatch({   
            type: 'UPD_COMMENT',
            id: this.props.comments.id,
            data: data
        });
    }

    render(){
        return(
            <div>
                <form onSubmit = {this.handlesubmit}>
                    <input required type="text" placeholder = "Enter Name" defaultValue ={this.props.comments.name} ref = {(input)=> this.getName = input} />
                    <br />
                    <br />
                    <textarea  rows="5" cols="28" placeholder="Messages" defaultValue ={this.props.comments.message} ref = {(input)=> this.getMessage = input} />
                    <br />
                    <br />
                    <button>Update</button>
                </form>
            </div>
        );
    };
}

export default connect()(EditComponent);
