import React from 'react';
import Comment from './Comment';
import EditComponent from './EditComponent';
import { connect } from 'react-redux';

class ShowComment extends React.Component{
    render(){
        return(
            <div>
                <h1>Show All Comment</h1>
                <table class="highlight">
                    <thead>
                        <tr>
                        <th>NAME</th>
                        <th>GENDER</th>
                        <th>MOBILE PHONE</th>
                        <th>NATIONALITY</th>
                        <th></th>
                        <th></th>
                        </tr>
                    </thead>
                    <tbody>
                    <tr>
                {console.log(this.props.commentcard)}
                {this.props.commentcard.map((comment) => (
                <div key = {comment.id}>
                    {comment.editing ? <td><EditComponent comment = {comment} key={comment.id} /></td> :
                    <td><Comment key={comment.id} comment = {comment} /></td>}
                </div>
                ))}
                </tr>
                </tbody>
                </table>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return{
        commentcard : state
    }
} 

export default connect(mapStateToProps)(ShowComment);
