import React from 'react';
import { connect } from 'react-redux';
import { Button } from 'react-bootstrap';

class CommentForm extends React.Component{
    handlesubmit = (e) => {
        e.preventDefault();
        const name = this.getName.value;
        const message = this.getMessage.value;
        const data = {
            id: new Date(),
            name,
            message,
            editing: false
        };
        this.props.dispatch({   
            type: 'ADD_COMMENT', 
            data
        });

        this.getName.value = '';
        this.getMessage.value = '';
    }

    render(){
        return(
            <div className='container'>
                <h1>Personal Information</h1>
                <div id = "Title">
                    <p>Title</p>
                    <select class="form-control">
                        <option>Mr.</option>
                        <option>Ms./Mrs.</option>
                    </select>
                </div>
                <div className='input-group' id = "Firstname">
                    <p>Firstname</p>
                    <input required type="text" placeholder = "Firstname" ref = {(input)=> this.getName = input} />
                </div>
                <div className='input-group' id = "Lastname">
                    <p>Lastname</p>
                    <input required type="text" placeholder = "Lastname" ref = {(input)=> this.getName = input} />
                </div>
                <form onSubmit = {this.handlesubmit}>
                    <input required type="text" placeholder = "Enter Name" ref = {(input)=> this.getName = input} />
                    <br />
                    <br />
                    <textarea  rows="5" cols="28" placeholder="Messages" ref = {(input)=> this.getMessage = input} />
                    <br />
                    <br />
                    <Button>Comment</Button>
                </form>
            </div>
        );
    };
}

export default connect()(CommentForm);
